import 'package:flutter_local_notifications/flutter_local_notifications.dart';
class NotificationService { final FlutterLocalNotificationsPlugin _plugin = FlutterLocalNotificationsPlugin(); Future<void> init() async { const android = AndroidInitializationSettings('@mipmap/ic_launcher'); final settings = InitializationSettings(android: android); await _plugin.initialize(settings); } Future<void> showReminder(String title, String body) async { await _plugin.show(0, title, body, NotificationDetails(android: AndroidNotificationDetails('vitalcare_channel', 'VitalCare', importance: Importance.high, priority: Priority.high))); } }
